import React, { useState } from "react";
import { DropdownButton, Dropdown, Button, Form } from "react-bootstrap";
import axios from "axios";
import authHeader from "../auth.js";
import { Container } from "react-bootstrap";

const StartProcess = ({ processNames, serverNames }) => {
  const [server, setServer] = useState("Select Server");
  const [process, setProcess] = useState("Select Process");
  const [displayProcess, setDisplayProcess] = useState([]);

  const handleSelectServer = (e) => {
    setServer(e);
    let i = 0;
    for (i = 0; i < serverNames.length; i++) {
      if (serverNames[i].serverName === e) break;
    }
    let selectedProcesses = [];
    for (let j = 0; j < serverNames[i].processIds.length; j++) {
      for (let k = 0; k < processNames.length; k++) {
        if (serverNames[i].processIds[j] === processNames[k]._id) {
          selectedProcesses.push(processNames[k].processName);
          console.log(processNames[k].processName);
        }
      }
    }
    setDisplayProcess(selectedProcesses);
  };

  const handleSelectProcess = (e) => {
    setProcess(e);
  };

  const onSubmit = (e) => {
    console.log("clicked");
    e.preventDefault();

    if (server === "Select Server" || process === "Select Process") {
      alert("please select server and process");
      return;
    }
    console.log(`from on submit server- ${server}`);
    console.log(`from on submit process- ${process}`);
    axios
      .post(
        "http://localhost:4000/api/server/processes/start",
        {
          pname: process,
          sname: server,
        },
        {
          headers: authHeader(),
        }
      )
      .then((res) => {
        alert("res from startprocess in frontend process started succesfully");
        console.log(`from on submit server- ${server}`);
        console.log(`from on submit process- ${process}`);
        console.log(
          "res from startprocess in frontend process started succesfully"
        );
      })
      .catch((err) => {
        alert("failed to start process");
      });

    setServer("Select Server");
    setProcess("Select Process");
  };

  return (
    /* <h3>Start Process</h3>
      <div>
        <form
          className="form-inline mx-auto d-inline-flex"
          style={{ alignContent: "center" }}
        >
          <div className="form-inline pt-4 ml-5 mr-2">
            <DropdownButton title={server} value={server}>
              {details.map((data, index) => (
                <Dropdown.Item
                  title={data.server}
                  key={index}
                  eventKey={data.server}
                  onSelect={handleSelectServer}
                >
                  {data.server}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </div>

          <div className="form-inline pt-4 mr-2">
            <DropdownButton
              className="custom-dd-button"
              title={process}
              value={process}
            >
              {details
                .filter((data) => server === data.server)
                .map((data) =>
                  data.process.map((process, index) => (
                    <Dropdown.Item
                      title={process}
                      key={index}
                      eventKey={process}
                      onSelect={handleSelectProcess}
                    >
                      {process}
                    </Dropdown.Item>
                  ))
                )}
            </DropdownButton>
          </div> */
    <Container>
      <Form>
        <Form.Row className="form">
          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Server Name</Form.Label>
            <Form.Control
              as="select"
              onChange={(e) => handleSelectServer(e.target.value)}
              value={server}
              className="form-control"
            >
              <option></option>
              {serverNames &&
                serverNames.map((s) => <option>{s.serverName}</option>)}
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label className="form-label">Process Name</Form.Label>
            <Form.Control
              as="select"
              onChange={(e) => handleSelectProcess(e.target.value)}
              value={process}
              className="form-control"
            >
              <option></option>
              {displayProcess &&
                displayProcess.map((p) => <option> {p} </option>)}
            </Form.Control>
          </Form.Group>

          <Button
            className="custom-btn-primary"
            variant="primary"
            onClick={onSubmit}
          >
            Start
          </Button>
        </Form.Row>
      </Form>
    </Container>
  );
};

export default StartProcess;
