import React, { useState } from "react";
import { DropdownButton, Dropdown, Button, Form } from "react-bootstrap";
import axios from "axios";
import authHeader from "../auth.js";
import { Container } from "react-bootstrap";

const StartProcess = ({ details }) => {
  const [server, setServer] = useState("Select Server");
  const [process, setProcess] = useState("Select Process");

  const handleSelectServer = (e) => {
    setServer(e);
  };

  const handleSelectProcess = (e) => {
    setProcess(e);
  };

  const onSubmit = (e) => {
    console.log("clicked");
    e.preventDefault();

    if (server === "Select Server" || process === "Select Process") {
      alert("please select server and process");
      return;
    }
    console.log(`from on submit server- ${server}`);
    console.log(`from on submit process- ${process}`);
    axios
      .post(
        "http://localhost:4000/api/server/processes/start",
        {
          pname: process,
          sname: server,
        },
        {
          headers: authHeader(),
        }
      )
      .then((res) => {
        alert("res from startprocess in frontend process started succesfully");
        console.log(`from on submit server- ${server}`);
        console.log(`from on submit process- ${process}`);
        console.log(
          "res from startprocess in frontend process started succesfully"
        );
      })
      .catch((err) => {
        alert("failed to start process");
      });

    setServer("Select Server");
    setProcess("Select Process");
  };

  return (
    /* <h3>Start Process</h3>
      <div>
        <form
          className="form-inline mx-auto d-inline-flex"
          style={{ alignContent: "center" }}
        >
          <div className="form-inline pt-4 ml-5 mr-2">
            <DropdownButton title={server} value={server}>
              {details.map((data, index) => (
                <Dropdown.Item
                  title={data.server}
                  key={index}
                  eventKey={data.server}
                  onSelect={handleSelectServer}
                >
                  {data.server}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </div>

          <div className="form-inline pt-4 mr-2">
            <DropdownButton
              className="custom-dd-button"
              title={process}
              value={process}
            >
              {details
                .filter((data) => server === data.server)
                .map((data) =>
                  data.process.map((process, index) => (
                    <Dropdown.Item
                      title={process}
                      key={index}
                      eventKey={process}
                      onSelect={handleSelectProcess}
                    >
                      {process}
                    </Dropdown.Item>
                  ))
                )}
            </DropdownButton>
          </div> */
    <Container>
      <Form>
        <Form.Row className="form">
          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Server Name</Form.Label>
            <Form.Control
              as="select"
              onChange={(e) => setServer(e.target.value)}
              value={server}
              className="form-control"
            >
              <option></option>
              {details.map((s) => (
                <option>{s.serverName}</option>
              ))}
            </Form.Control>
          </Form.Group>

          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label className="form-label">Process Name</Form.Label>
            <Form.Control
              as="select"
              onChange={(e) => setProcess(e.target.value)}
              value={process}
              className="form-control"
            >
              <option></option>
              {details
                .filter((data) => server === data.server)
                .map((data) =>
                  data.process.map((process, index) => (
                    <option> {process}</option>
                  ))
                )}
            </Form.Control>
          </Form.Group>
        </Form.Row>
        <Button
          className="custom-btn-primary"
          variant="primary"
          onClick={onSubmit}
        >
          Start
        </Button>
      </Form>
    </Container>
  );
};

export default StartProcess;
